function Nchart_simplebar(chart_id, chart_data, chart_scroll_bar) {
	var chart = AmCharts.makeChart(chart_id, {
		"type": "serial",
		"theme": "light",
		"dataProvider": chart_data,
		"valueAxes": [ {
			"gridColor": "#FFFFFF",
			"gridAlpha": 0.2,
			"dashLength": 0
		} ],
		"startDuration": 1,
		"graphs": [ {
			"id": "g1",
			"balloonText": "[[category]]: <b>[[value]]</b>",
			"fillAlphas": 0.8,
			"lineAlpha": 0.2,
			"fillColors": "#d97df0",
			"type": "column",
			"valueField": "y-value"
		} ],
		"chartCursor": {
			"categoryBalloonEnabled": false,
			"cursorAlpha": 0,
			"zoomable": false
		},
		"chartScrollbar": {
			"enabled": chart_scroll_bar,
			"autoGridCount": true,
			"graph": "g1",
			"scrollbarHeight": 40,
			"selectedBackgroundColor":"#e4e2e2",
            "selectedGraphFillColor":"#d97df0",
            "backgroundColor":"#fff"
		},
		"categoryField": "x-value",
		"categoryAxis": {
			"gridPosition": "start",
			"gridAlpha": 0,
			"tickPosition": "start",
			"tickLength": 20
		},
		"export": {
			"enabled": false
		},
	} );
}