
$('#closeicon2').click(function () {
    setTimeout(function () {
       if($('#closeicon2').is(":checked") == true){
            $('#close_icon').find('i').toggleClass('fa-caret-right fa-times-circle')
            $('#menuOverlay').css({
                'background': '#00000096',
                'display': 'block',
                'height': '100%',
                'width': '100%'
            });
            $(".page-container").css({
                'z-index': '-1'
        });
       }else{
            $('#close_icon').find('i').toggleClass('fa-times-circle fa-caret-right')
             $('#menuOverlay').removeAttr("style");
       }

    }, 200);
});

$('#menuOverlay').click(function () {
    setTimeout(function () {
        if ($('#Cmenu_circle2').css('opacity') == 1) {
            $('#menuOverlay').removeAttr("style");
            $(".page-container").css({
                'z-index': ''
            });
            $('#closeicon1').find('i').toggleClass('fa-caret-left fa-times-circle')
            $('#Cmenu_circle2').css({
                '-webkit-transform': 'scale(0)',
                '-moz-transform': 'scale(0)',
                '-ms-transform': 'scale(0)',
                '-o-transform': 'scale(0)',
                'transform': 'scale(0)'
            });
            $('#Cmenu_circle1').css('opacity', '0')
        } else {
            console.log('Not Show')
        }
    }, 300);
});




