//custom function based on screen start (Tru)
function responsiveDivOpen(){
	 if(w>1100 && w < 1500){
	  $('#banner_fullwidth').removeClass("col-md-9");
	  $('#banner_fullwidth').removeClass("col-md-10");
	  $('#banner_fullwidth').removeClass("col-md-12");
	  $('#chat_box').removeClass("col-md-7");
	  $('#chat_box').removeClass("col-md-5");
	  $('#banner_fullwidth').addClass("col-md-8");
	  $('#chat_box').addClass("col-md-7");
	 }else if(w<=1100){
      $('#banner_fullwidth').removeClass("col-md-9");
	  $('#banner_fullwidth').removeClass("col-md-10");
	  $('#banner_fullwidth').removeClass("col-md-12");
	  $('#chat_box').removeClass("col-md-7");
	  $('#chat_box').removeClass("col-md-5");
	  $('#banner_fullwidth').addClass("col-md-7");
	  $('#chat_box').addClass("col-md-7");
	  }
}

function responsiveDivClose(){
	$('#banner_fullwidth').removeClass("col-md-8").addClass("col-md-12");
}
